            current: false,
            next: false
        };
        this.buttonTextState = 'shown';
        // The text currently being show
        this.buttonText = "ADD TO CART";
        // The text that will be shown when the transition is finished
        this.transitionButtonText = "ADD TO CART";
        this.display = false;
